import { BottomButton } from "./styledComponents/BottomButton";
import { RoundButton } from "./styledComponents/RoundButton";
import { Skip } from "./styledComponents/Skipbutton";
import styled from "styled-components";
import { useState, useEffect } from "react";

const Maindiv = styled.div`
  width: 100%;
`;

const Para = styled.p`
  font-family: "Rubik", sans-serif;
  font-style: normal;
  font-weight: normal;
  font-size: 20px;
  line-height: 25px;
  /* identical to box height, or 125% */
  margin-bottom: 130px;
  margin-top: 75px;
  text-align: center;
  letter-spacing: 0.02em;

  color: #495057;
`;

export const PageTwo = (props) => {
  const [bottomButtonText, setBottomButtonText] = useState("NEXT");
  const [isSelected, setIsSelected] = useState(false);
  useEffect(() => {
    isSelected ? setBottomButtonText("FINISH") : setBottomButtonText("NEXT");
  }, [isSelected]);

  const ButtonWrapDiv = styled.div`
    margin-top: 90px;
    display: flex;
    justify-content: center;
    & button {
      color: ${isSelected ? "white" : " #149f65"};
      background-color: ${isSelected ? " #149f65" : "white"};
      border: ${isSelected ? "none" : "solid #CED4DA 1px"};
    }
  `;

  const handleIsSelected = () => {
    setIsSelected(true);
  };

  const RoundButtonDiv = styled.div`
    display: flex;
    justify-content: space-evenly;
    margin-top: -15px;
  `;

  return (
    <Maindiv>
      <Skip onclick={props.handelSkip} />
      <Para>What are you interested in?</Para>

      <RoundButtonDiv>
        {" "}
        <RoundButton handleIsSelected={handleIsSelected} isBig={true}>
          Home & Lifestyle
        </RoundButton>
        <RoundButton handleIsSelected={handleIsSelected} isBig={true}>
          Food & Kitchen
        </RoundButton>
      </RoundButtonDiv>
      <RoundButtonDiv>
        {" "}
        <RoundButton handleIsSelected={handleIsSelected} isBig={false}>
          Beauty & Wellness
        </RoundButton>
        <RoundButton handleIsSelected={handleIsSelected} isBig={false}>
          Pets
        </RoundButton>
        <RoundButton handleIsSelected={handleIsSelected} isBig={true}>
          Food & Kitchen
        </RoundButton>
      </RoundButtonDiv>
      <RoundButtonDiv>
        {" "}
        <RoundButton handleIsSelected={handleIsSelected} isBig={true}>
          Fashion
        </RoundButton>
        <RoundButton handleIsSelected={handleIsSelected} isBig={false}>
          Art & Design
        </RoundButton>
        <RoundButton handleIsSelected={handleIsSelected} isBig={false}>
          Health & Fitness
        </RoundButton>
      </RoundButtonDiv>
      <RoundButtonDiv>
        {" "}
        <RoundButton handleIsSelected={handleIsSelected} isBig={false}>
          The World
        </RoundButton>
        <RoundButton handleIsSelected={handleIsSelected} isBig={false}>
          News
        </RoundButton>
        <RoundButton handleIsSelected={handleIsSelected} isBig={true}>
          DIY crafts
        </RoundButton>
      </RoundButtonDiv>
      <RoundButtonDiv>
        {" "}
        <RoundButton handleIsSelected={handleIsSelected} isBig={false}>
          Blockchain
        </RoundButton>
        <RoundButton handleIsSelected={handleIsSelected} isBig={false}>
          Programming
        </RoundButton>
        <RoundButton handleIsSelected={handleIsSelected} isBig={false}>
          Beauty & Wellness
        </RoundButton>
      </RoundButtonDiv>
      <RoundButtonDiv>
        {" "}
        <RoundButton handleIsSelected={handleIsSelected} isBig={false}>
          Home & Lifestyle
        </RoundButton>
        <RoundButton handleIsSelected={handleIsSelected} isBig={true}>
          Food & Kitchen
        </RoundButton>
      </RoundButtonDiv>

      <ButtonWrapDiv>
        {" "}
        <BottomButton
          yes={"yes"}
          isSelected={isSelected}
          onclick={props.handelPage}
        >
          {" "}
          {bottomButtonText}
        </BottomButton>
      </ButtonWrapDiv>
    </Maindiv>
  );
};
