    border-radius: 10px;
  }
  & > :nth-child(2) {
    font-family: "Segoe UI", sans-serif;
    font-style: normal;
    font-weight: bold;
    font-size: 14px;
    line-height: 21px;
    /* identical to box height, or 150% */

    letter-spacing: -0.32px;

    color: #ffffff;
  }
  & > :nth-child(3) {
    font-family: "Segoe UI", sans-serif;
    font-style: normal;
    font-weight: bold;
    font-size: 12px;
    line-height: 21px;
    /* identical to box height, or 175% */

    letter-spacing: -0.32px;

    color: #ffffff;
  }
`;
const HousingDiv = styled.div`
  display: grid;
  grid-template-rows: 65% 15% 15%;
  grid-gap: 3%;
  align-items: end;
  justify-content: center;
  text-align: center;

  /* background-color: #6e5e5e; */
  & > :nth-child(1) {
    height: 9%;
    margin: auto;
    margin-bottom: 0px;
    background-color: #3c89ff;
    width: 9px;
    border-radius: 10px;
  }
  & > :nth-child(2) {
    font-family: "Segoe UI", sans-serif;
    font-style: normal;
    font-weight: bold;
    font-size: 14px;
    line-height: 21px;
    /* identical to box height, or 150% */

    letter-spacing: -0.32px;

    color: #ffffff;
  }
  & > :nth-child(3) {
    font-family: "Segoe UI", sans-serif;
    font-style: normal;
    font-weight: bold;
    font-size: 12px;
    line-height: 21px;
    /* identical to box height, or 175% */

    letter-spacing: -0.32px;

    color: #ffffff;
  }
`;
const DietDiv = styled.div`
  display: grid;
  grid-template-rows: 65% 15% 15%;
  grid-gap: 3%;
  align-items: end;
  justify-content: center;
  text-align: center;

  /* background-color: #6e5e5e; */
  & > :nth-child(1) {
    height: 7%;
    margin: auto;
    margin-bottom: 0px;
    background-color: #3c89ff;
    width: 9px;
    border-radius: 10px;
  }
  & > :nth-child(2) {
    font-family: "Segoe UI", sans-serif;
    font-style: normal;
    font-weight: bold;
    font-size: 14px;
    line-height: 21px;
    /* identical to box height, or 150% */

    letter-spacing: -0.32px;

    color: #ffffff;
  }
  & > :nth-child(3) {
    font-family: "Segoe UI", sans-serif;
    font-style: normal;
    font-weight: bold;
    font-size: 12px;
    line-height: 21px;
    /* identical to box height, or 175% */

    letter-spacing: -0.32px;

    color: #ffffff;
  }
`;
const MobilityDiv = styled.div`
  display: grid;
  grid-template-rows: 65% 15% 15%;
  grid-gap: 3%;
  align-items: end;
  justify-content: center;
  text-align: center;

  /* background-color: #6e5e5e; */
  & > :nth-child(1) {
    height: 2%;
    margin: auto;
    margin-bottom: 0px;
    background-color: #3c89ff;
    width: 9px;
    border-radius: 10px;
  }
  & > :nth-child(2) {
    font-family: "Segoe UI", sans-serif;
    font-style: normal;
    font-weight: bold;
    font-size: 14px;
    line-height: 21px;
    /* identical to box height, or 150% */

    letter-spacing: -0.32px;

    color: #ffffff;
  }
  & > :nth-child(3) {
    font-family: "Segoe UI", sans-serif;
    font-style: normal;
    font-weight: bold;
    font-size: 12px;
    line-height: 21px;
    /* identical to box height, or 175% */

    letter-spacing: -0.32px;

    color: #ffffff;
  }
`;

const HeadingMoreOptions = styled.p`
  font-family: "DM Sans", sans-serif;
  font-style: normal;
  font-weight: bold;
  font-size: 20px;
  line-height: 26px;
  /* identical to box height */
  margin-top: 65px;
  color: #ffffff;
`;
const MoreOptionsWrapper = styled.div`
  background: #ffffff;
  border-radius: 8px;
  height: 84px;
  margin-top: 16px;
  display: grid;
  grid-template-columns: 90% 10%;
  justify-content: center;
  align-items: center;
  & > :nth-child(1) {
    font-family: "Segoe UI", sans-serif;
    font-style: normal;
    font-weight: normal;
    font-size: 14px;
    line-height: 21px;
    letter-spacing: 0px;
    width: 90%;
    margin: auto;
    color: #212529;
    /* background-color: #856c6c; */
  }
  & > :nth-child(2) {
    /* background-color: #856c6c; */
    align-items: center;
    margin-right: 9px;
    & img {
      width: 100% !important;
      margin: none;
      background-color: white;
    }
  }
`;
const MoreOptionsButton = styled.div`
  border: none;
  background-color: none;
  height: 24px;
  width: 24px;
`;
export const AnnualCarbonFootprintShow = (props) => {
  const [carbonValue, setCarbonValue] = useState(0);

  // console.log(carbonValue, "carb.......................................");
  useEffect(() => {
    setCarbonValue(props.carbonFootprint.toFixed(2));
  }, []);
  const CarbonDataBar = styled.div`
    /* background-color: white; */
    height: 68px;
    width: ${() => {
      //console.log(Number(carbonValue));
      let percentage = (Number(carbonValue) / 4.5) * 100;
      //console.log(percentage);

      return `${percentage}%`;
    }};
    margin-top: 3px;
    & :nth-child(1) {
      height: 9px;
      background-color: #feb700;
      border-radius: 4px;
    }
    & :nth-child(2) {
      float: right;
      margin-top: 5px;
      height: 26px;
      width: 85px;
      text-align: center;
      background-color: #ffffff;
      border-radius: 8px 0px 8px 8px;
    }
  `;
  const NextButton = styled.div`
    width: 146px;
    margin: auto;
    height: 56px;
    margin-top: 50px;

    background: #25ce7b;
    box-shadow: 0px 4px 90px #000000;
    border-radius: 4px;
    font-family: "DM Sans", sans-serif;
    font-style: normal;
    font-weight: bold;
    font-size: 16px;
    line-height: 21px;
    /* identical to box height, or 131% */

    display: flex;
    align-items: center;
    justify-content: center;
    letter-spacing: -0.32px;

    color: #ffffff;
    /* margin-bottom: 34px; */
  `;

  return (
    <MainWrapperDiv>
      <SecondWrapperDiv>
        <CloseButton></CloseButton>
        <StaticP>YOUR ANNUAL CARBON FOOTPRINT</StaticP>
        <CarbonValue>{carbonValue}</CarbonValue>
        <TonsP>Tons of CO2</TonsP>
        <HorizontalBarsDiv>
          <LeftInHorDiv>
            <p>You</p>
            <p>Avg India</p>
            <p>Avg World</p>
          </LeftInHorDiv>
          <RightInHorDiv>
            <CarbonDataBar>
              <div></div>
              <div>{carbonValue} Tons</div>
            </CarbonDataBar>
            <BelowCarbonDataBar>
              <div>
                <div></div>
                <div>1.75 Tons</div>
              </div>
              <div>
                <div></div>
                <div>4.50 Tons</div>
              </div>
            </BelowCarbonDataBar>
          </RightInHorDiv>
        </HorizontalBarsDiv>
        <HeadingUnderstand>Understand your footprint</HeadingUnderstand>
        <VerticalBarsDiv>
          <FlyingDiv>
            <div></div>
            <div>Flying</div>
            <div>69%</div>
          </FlyingDiv>
          <SpendingDiv>
            {" "}
            <div></div>
            <div>Spending</div>
            <div>13%</div>
          </SpendingDiv>
          <HousingDiv>
            {" "}
            <div></div>
            <div>Housing</div>
            <div>9%</div>
          </HousingDiv>
          <DietDiv>
            {" "}
            <div></div>
            <div>Diet</div>
            <div>7%</div>
          </DietDiv>
          <MobilityDiv>
            {" "}
            <div></div>
            <div>Mobility </div>
            <div>2%</div>
          </MobilityDiv>
        </VerticalBarsDiv>

        <HeadingMoreOptions>More options</HeadingMoreOptions>
        <MoreOptionsWrapper>
          <div>
            Missed something? Want to share more details about your footprint?
          </div>
          <MoreOptionsButton>
            <div>
              <img src={svg} alt="" />{" "}
            </div>
          </MoreOptionsButton>
        </MoreOptionsWrapper>
        <MoreOptionsWrapper>
          <div>Reset all questions and restart the calculator</div>
          <MoreOptionsButton>
            <div>
              <img src={svg} alt="" />{" "}
            </div>
          </MoreOptionsButton>
        </MoreOptionsWrapper>
        <NextButton>
          <p>Next </p>
        </NextButton>
      </SecondWrapperDiv>
    </MainWrapperDiv>
  );
};
