import { Carousel } from "react-responsive-carousel";
import mobilityImage from "./img/mobility.png";
import shelterimage from "./img/shelter.png";
import foodImage from "./img/food.png";
import "./SliderCss.css";
import { CloseButton } from "./styledComponents/CloseButtonInCalculation";
import styled from "styled-components";
import { useEffect } from "react/cjs/react.development";

const HeadingStrategyDiv = styled.div`
  text-align: center;
  margin-top: -35px;
  font-family: "Segoe UI", sans-serif;
  font-style: normal;
  font-weight: bold;
  font-size: 26px;
  line-height: 35px;
  /* identical to box height */

  letter-spacing: -0.32px;

  /* Gray - 900 */

  color: #212529;
`;

const Para = styled.p`
  text-align: center;
  width: 292px;
  height: 52px;
  margin: auto;
  font-family: "DM Sans", sans-serif;
  font-style: normal;
  font-weight: normal;
  font-size: 20px;
  line-height: 26px;
  text-align: center;
  /* letter-spacing: -0.32px; */

  /* Gray - 800 */
  margin-top: 76px;
  color: #343a40;
`;

const AddButton = styled.div`
  width: 93px;
  height: 43px;
  border-radius: 34px;
  display: flex;
  /* z-index: 100000000000000000; */
  /* margin: auto; */
