  width: 93px;
  height: 43px;
  left: 205px;
  top: 70px;
  font-family: "Segoe UI", sans-serif;
  font-style: normal;
  font-weight: bold !important;
  font-size: 20px;
  line-height: 24px;
  /* identical to box height */

  text-align: center;
  letter-spacing: -0.308657px;

  color: #ffffff;
`;
const SliderWrapDiv = styled.div`
  width: 87%;
  margin: auto;
`;
const Label = styled.div`
  margin-top: -245px;
  font-family: "Segoe UI", sans-serif;
  font-style: normal;

  font-size: 50px;
  line-height: 67px;
  /* identical to box height */

  text-align: center;
  /* letter-spacing: -0.429699px; */
  font-weight: 800 !important;
  color: #ffffff;
`;
const LearnMore = styled.div`
  margin-top: 40px;
  font-family: "Segoe UI", sans-serif;
  font-style: normal;

  font-size: 18px;
  line-height: 24px;
  /* identical to box height */
  font-weight: 700 !important;
  text-align: center;

  color: #ffffff;
`;
const Spacer = styled.div`
  height: 135px;
`;
const AllCategories = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  padding: 12px 32px;
  width: 235px;
  height: 64px;
  margin: auto;
  background: #2c7fff;
  border-radius: 8px;
  margin-top: -20px;
  font-family: "DM Sans", sans-serif;
  font-style: normal;
  font-weight: bold;
  font-size: 18px;
  line-height: 23px;
  letter-spacing: -0.1040752px;

  color: #ffffff;
`;
