    <>
      <CloseButton color="black"></CloseButton>
      <HeadingStrategyDiv>Strategy</HeadingStrategyDiv>
      <Para>
        How do you want to neutralize your {props.carbonFootprint} ton carbon
        footprint?
      </Para>
      <SliderWrapDiv>
        {" "}
