          <div>
            <AddButton
              onClick={() => {
                props.handleStrategySelection("mobility");
              }}
            >
              {" "}
              {props.strategySelection.food ? "Added" : "+ Add"}
            </AddButton>
            <img src={mobilityImage} alt="" />
            <Label>Mobility</Label>
            <LearnMore>Learn More ►</LearnMore>
          </div>
          <div>
            <AddButton>
              {" "}
              {props.strategySelection.food ? "Added" : "+ Add"}
            </AddButton>
            <img src={shelterimage} alt="" />
            <Label>Shelter</Label>
            <LearnMore>Learn More ►</LearnMore>
          </div>
          <div>
            <AddButton>
              {props.strategySelection.food ? "Added" : "+ Add"}
            </AddButton>
            <img src={foodImage} alt="" />
            <Label>Food</Label>
            <LearnMore>Learn More ►</LearnMore>
            <Spacer></Spacer>
          </div>
        </Carousel>
      </SliderWrapDiv>

      <AllCategories>Select all categories</AllCategories>
    </>
  );
}

