import { useState } from "react/cjs/react.development";
import styled from "styled-components";

export function RoundButton(props) {
  // console.log(props.isSelected);

  // const [sel, setSel] = useState(false);
  // props.isSelected && setSel(true);

  const [selected, setSelected] = useState(false);

  const Button = styled.button`
    /* padding: 6px; */
    height: 34px;
    display: flex;
    align-items: center;
    justify-content: center;
    border: 1px solid #6c757d;
    /* background-color: #ffffff; */
    border-radius: 14px;
    margin-bottom: 56px;
    /* text........ */
    font-family: "Rubik", sans-serif;
    font-style: normal;
    font-weight: 500;
    font-size: ${props.isBig ? "16px" : "14px"};
    line-height: 16px;
    letter-spacing: 1.25px;
    /* text-transform: uppercase; */

    /* color: #212121; */
    color: ${selected ? "white" : " #212121"};
    background-color: ${selected ? " #149f65" : "#ffffff"};
    border: ${selected ? "none" : "solid ##6c757d 1px"};
  `;

  return <Button onClick={setSelected(true)}> {props.children}</Button>;
}
