import { useState } from "react/cjs/react.development";
import styled from "styled-components";
import backImg from "./img/background.png";
import { CloseButton } from "./styledComponents/CloseButtonInCalculation";
const MainWrapperDiv = styled.div`
  background-image: url(${backImg});
  height: 1204px;
`;
const SecondWrapperDiv = styled.div`
  width: 90%;
  margin: auto;
`;
const StaticP = styled.p`
  text-align: center;
  font-family: "Segoe UI", sans-serif;
  font-style: normal;
  font-weight: bold;
  font-size: 14px;
  line-height: 19px;
  /* identical to box height */

  letter-spacing: 0.15em;
  margin-top: 20px;
  color: #ffffff;
`;

const CarbonValue = styled.p`
  text-align: center;
  font-family: "DM Sans", sans-serif;
  font-style: normal;
  font-weight: bold;
  font-size: 70px;
  line-height: 91px;
  /* identical to box height */

  /* letter-spacing: 0.15em; */
  margin-top: 25px;
  color: #ffffff;
`;
const TonsP = styled.p`
  text-align: center;
  margin-top: -5px;
  color: #ffffff;
  font-family: "DM Sans", sans-serif;
  font-style: normal;
  font-weight: 500;
  font-size: 22px;
  line-height: 29px;
  /* identical to box height */

  color: #ffffff;
`;
const HorizontalBarsDiv = styled.div`
  background-color: #3b3b3b;
  height: 155px;
  margin-top: 30px;
  display: grid;
  grid-template-columns: 27% 72%;
  grid-gap: 1%;
`;
const LeftInHorDiv = styled.div`
  /* background-color: black; */
  height: 100px;
  & p {
    font-family: "DM Sans", sans-serif;
    font-style: normal;
    font-weight: bold;
    font-size: 16px;
    line-height: 21px;
    color: #ffffff;
    margin-bottom: 45px;
  }
`;
const RightInHorDiv = styled.div`
  background-color: black;
  height: 100px;
`;
const CarbonDataBar = styled.div`
  background-color: white;
  height: 68px;
  width: 80%;
  & :nth-child(1) {
    height: 8px;
    background-color: #feb700;
    border-radius: 4px;
  }
  & :nth-child(2) {
    height: 8px;
    background-color: #ffffff;
    border-radius: 4px;
  }
`;
const BelowCarbonDataBar = styled.div`
  background-color: white;
  height: 136px;
  & div {
    height: 68px;
    border-bottom: 1px solid black;
  }
`;
console.log(backImg);
export const AnnualCarbonFootprintShow = () => {
  const [carbonValue, setCarbonValue] = useState("2.90");

  return (
    <MainWrapperDiv>
      <SecondWrapperDiv>
        <CloseButton></CloseButton>
        <StaticP>YOUR ANNUAL CARBON FOOTPRINT</StaticP>
        <CarbonValue>{carbonValue}</CarbonValue>
        <TonsP>Tons of CO2</TonsP>
        <HorizontalBarsDiv>
          <LeftInHorDiv>
            <p>You</p>
            <p>Avg India</p>
            <p>Avg World</p>
          </LeftInHorDiv>
          <RightInHorDiv>
            <CarbonDataBar>
              <div></div>
              <div>{carbonValue}</div>
            </CarbonDataBar>
            <BelowCarbonDataBar>
              <div></div>
              <div></div>
            </BelowCarbonDataBar>
          </RightInHorDiv>
        </HorizontalBarsDiv>
      </SecondWrapperDiv>
    </MainWrapperDiv>
  );
};
